$(function(){var k={success:1,userNotExist:2,pwdError:3};var c=$("#user"),b=$("#pwd"),g=$(".login-tips"),h=$(".login-true"),e=$(".login-false"),j=$(".login-user"),f=$(".panelA"),d=$(".panelB"),i=false;function a(){$(".login-box").hide();g.text("");c.val("");b.val("")}$(".login-btn").click(function(){$(".login-box").show();if(i){f.hide();d.show()}else{d.hide();f.show();c.focus()}});e.click(a);h.click(function(){if(i){a();return}var l=c.val(),m=b.val();if($.trim(l)==""){g.text("用户名不能为空！");c.focus();return}if($.trim(m)==""){g.text("密码不能为空！");b.focus();return}$.ajax({url:"/ajax/login.php",type:"POST",data:{user:l,pwd:m},dataType:"json",success:function(n){if(n.result==k.success){a();j.text(n.name);i=true}else{if(n.result==k.pwdError){g.text("密码错误！");b.focus().select()}else{if(n.result==k.userNotExist){g.text("用户不存在！");c.focus().select()}}}},error:function(n,p,o){console.error("ajax error!"+p)}})})});